content.query:
let alertText =  strcat_array(dynamic([{RuleId}]),",");
let getAmountOfIncidentForRuleId = (classification:string){
    SecurityIncident
    | where TimeGenerated >= ago(30d)
    | where Classification == classification
    | mv-expand AlertId=AlertIds
    | extend AlertId=tostring(AlertId)
    | join  (SecurityAlert| where TimeGenerated >=ago(30d)) on $left.AlertId==$right.SystemAlertId
    | mv-expand RuleId=RelatedAnalyticRuleIds
    | extend RuleId=iff(ProductName!= 'Azure Sentinel', ProductName,RuleId)
    | summarize counter=count() by RuleIdentifier=tostring(RuleId)
    | extend RuleId=RuleIdentifier
    | project-away RuleIdentifier
};
let falsePositiveClassificationTable = getAmountOfIncidentForRuleId("FalsePositive") | extend FalsePositiveCounter=counter | project-away counter;
let undeterminedClassificationTable = getAmountOfIncidentForRuleId("Undetermined") | extend UndeterminedCounter=counter | project-away counter;
let benignPositiveClassificationTable = getAmountOfIncidentForRuleId("BenignPositive") | extend BenignPositiveCounter=counter | project-away counter;
let truePositiveClassificationTable = getAmountOfIncidentForRuleId("TruePositive") | extend TruePositiveCounter=counter | project-away counter;
let closedIncidentTable = SecurityIncident| where TimeGenerated >= ago(30d) |where Status == "Closed" | mv-expand AlertId=AlertIds| extend AlertId=tostring(AlertId)| join  SecurityAlert on $left.AlertId==$right.SystemAlertId| mv-expand RelatedAnalyticRuleIds| extend RuleId= iff(ProductName == 'Azure Sentinel', tostring(RelatedAnalyticRuleIds), ProductName);
let joinByRuleId = (T:(RuleId:string), S:(RuleId:string)){
    T 
    | join kind=fullouter S on $left.RuleId == $right.RuleId
    | extend RuleId= iff(RuleId == '', RuleId1,RuleId)
    | project-away RuleId1
};
joinByRuleId(joinByRuleId(joinByRuleId(joinByRuleId(falsePositiveClassificationTable, undeterminedClassificationTable) , benignPositiveClassificationTable), truePositiveClassificationTable),closedIncidentTable)
| join kind=leftouter  (SecurityAlert
| where TimeGenerated >= ago(30d)
| where ProductName  ==  'Azure Sentinel'
| extend RuleId = parsejson( tostring(todynamic(ExtendedProperties)['Analytic Rule Ids']))
| mv-expand RuleId=RuleId
| extend RuleId=tostring(RuleId)
| extend RuleName=  tostring(todynamic(ExtendedProperties)['Analytic Rule Name'])
| project RuleId,RuleName
| distinct RuleId,RuleName)
 on $left.RuleId==$right.RuleId
| extend RuleName=iff(isempty(RuleName),RuleId,RuleName)
| project-away RuleId1
| where alertText has RuleId 
| summarize dcount(IncidentNumber) by Classification
